import React, { useState, useRef } from "react";
import { ChatFeed } from "./ChatFeed";
import { ChatHeader } from "./ChatHeader";
import { CHAT_TYPE } from "../../constants/index";
import { ButtonWithSpinner } from "../common/button";
import { useSendMessage } from "./hooks/useSendMessage";
import "./Chat.css";

type Props = {
  chatType: CHAT_TYPE;
  id: string;
  name: string;
};

export const Chat = React.memo(({ chatType, id, name }: Props) => {
  const [message, setMessage] = useState<string>("");
  const chatFeedRef = useRef<{ refreshFeed: () => {} }>(null);
  const { isSendingMessage, sendMessage } = useSendMessage();

  async function handleMessageSubmit(e: React.FormEvent<HTMLFormElement>) {
    e.preventDefault();

    await sendMessage({
      type: chatType,
      to: id,
      message,
    });

    setMessage("");
  }

  return (
    <div className="chat">
      <ChatHeader id={id} chatType={chatType} name={name} />
      <ChatFeed chatType={chatType} id={id} ref={chatFeedRef} />
      <footer className="chat__footer">
        <form onSubmit={handleMessageSubmit}>
          <input
            className="chat__message-input"
            placeholder="Message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <ButtonWithSpinner
            isLoading={isSendingMessage}
            disabled={message === ""}
            type="submit"
          >
            Send
          </ButtonWithSpinner>
        </form>
      </footer>
    </div>
  );
});
