import { useUser } from "../../contexts/UserContext";
import { useAddUserDmMutation } from "./hooks/useAddUserDmMutation";
import { AddUser } from "../AddUserForm";

export function AddUserDm({ id }: { id: string }) {
  const [user] = useUser();
  const { isError, isSuccess, isLoading, error, mutate } =
    useAddUserDmMutation(id);

  return (
    <AddUser
      isError={isError}
      isSuccess={isSuccess}
      isLoading={isLoading}
      error={error}
      onAddUser={mutate}
    />
  );
}
