import { Spinner } from "../spinner/index";
import "./Button.css";

type ButtonWithSpinnerProps = {
  children: React.ReactNode;
  isLoading: boolean;
  className?: string;
  disabled?: boolean;
  [propName: string]: any;
};

export function ButtonWithSpinner({
  children,
  isLoading,
  className,
  disabled,
  ...restProps
}: ButtonWithSpinnerProps) {
  return (
    <button className={`btn ${className}`} disabled={disabled} {...restProps}>
      {children}
      {isLoading && <Spinner />}
    </button>
  );
}
