import userLogo from "../../assets/user.png";
import addUserIcon from "../../assets/add-user.png";
import { CHAT_TYPE } from "../../constants";
import { ChannelMembersModal } from "./ChannelMembersModal";
import { useBoolean } from "../../hooks/useBoolean";
import { AddUserChannelModal } from "./AddUserChannelModal";
import { useWindow } from "../../hooks/useWindow";
import userIcon from "../../assets/user.png";

type ChatHeaderProps = {
  name: string;
  id: string;
  chatType: CHAT_TYPE;
};

export function ChatHeader({ name, chatType, id }: ChatHeaderProps) {
  const {
    value: isAddUserChannelModalOpen,
    setTrue: openAddUserChannelModal,
    setFalse: closeAddUserChannelModal,
  } = useBoolean(false);
  const {
    value: isChannelMembersModalOpen,
    setTrue: openChannelMembersModal,
    setFalse: closeChannelMembersModal,
  } = useBoolean(false);

  return (
    <header className="chat__header">
      <img className="chat__icon" src={userLogo} alt="user-img" />
      <p>{name}</p>
      {chatType === CHAT_TYPE.CHANNEL && (
        <>
          <span className="chat__members" onClick={openChannelMembersModal}>
            <img className="chat-members__icon" src={userIcon} alt="user-img" />
            <p className="chat__members-count">{3}</p>
          </span>
          <ChannelMembersModal
            channelId={id}
            isOpen={isChannelMembersModalOpen}
            onClose={closeChannelMembersModal}
          />
          <img
            className="add-user__icon"
            src={addUserIcon}
            alt="user-img"
            onClick={openAddUserChannelModal}
          />
          <AddUserChannelModal
            isOpen={isAddUserChannelModalOpen}
            onClose={closeAddUserChannelModal}
            channelId={id}
          />
        </>
      )}
    </header>
  );
}
