import { useMutation } from "../../../hooks/useMutation";
import { addUserDm } from "../../../helpers";
import { ReqError } from "../../../types/index";

type AddUserReq = {
  username: string;
};

export function useAddUserDmMutation(userId: string) {
  const mutationObj = useMutation<any, ReqError, AddUserReq>(
    (addUserReq: AddUserReq) => {
      return addUserDm(userId, addUserReq);
    }
  );

  return mutationObj;
}
