import { useQuery } from "../../hooks/useQuery";
import { Portal } from "../Portal";
import {
  Modal,
  ModalOpenButton,
  ModalDismissButton,
  ModalContents,
} from "../modal/index";
import { getChannelMembers, addUserToChannel } from "../../helpers/index";
import { ConnectionType } from "../../types";
import { List, ListItem } from "../common/list/index";
import userIcon from "../../assets/user.png";

type Props = {
  channelId: string;
};

export function ChannelMembers({ channelId }: Props) {
  const { data: members } = useQuery<ConnectionType[]>(
    [channelId],
    ({ signal }) => getChannelMembers(channelId, { signal })
  );

  // function addUser(userId: string) {
  //   addUserToChannel(channelId, userId).then(() => {
  //     refreshMembers();
  //   });
  // }

  return (
    <Modal>
      <ModalOpenButton>
        <span className="chat__members">
          <img className="chat-members__icon" src={userIcon} alt="user-img" />
          <p className="chat__members-count">{members?.length}</p>
        </span>
      </ModalOpenButton>
      <Portal>
        <ModalContents>
          <div className="modal-container">
            <div className="chat__members-modal">
              <header className="chat__members-modal-hedear">
                <h3>Members</h3>
                <ModalDismissButton>
                  <button>X</button>
                </ModalDismissButton>
              </header>
              <List>
                {members?.map((member) => (
                  <ListItem
                    key={member.id}
                    style={{ paddingLeft: "20px", height: "60px" }}
                  >
                    <img
                      className="chat-members__icon"
                      src={userIcon}
                      alt="user-icon"
                    />
                    <p>{member.name}</p>
                  </ListItem>
                ))}
              </List>
            </div>
          </div>
        </ModalContents>
      </Portal>
    </Modal>
  );
}
