import React, { useState } from "react";
import { useUser } from "../../contexts/UserContext";
import { useMutation } from "../../hooks/useMutation";
import { ButtonWithSpinner } from "../common/button/index";
import { ErrorMessage, SuccessMessage } from "../common/Messages";

export function AddUser(mutationFn: any) {
  const [user] = useUser();
  const [username, setUsername] = useState<string>("");
  const { isLoading, isError, error, isSuccess, mutate } = useMutation<
    any,
    any
  >(mutationFn);

  function onChange(e: React.ChangeEvent<HTMLInputElement>) {
    setUsername(e.target.value);
  }

  function handleAddUser(e: React.FormEvent<HTMLFormElement>) {
    e.preventDefault();
    mutate(username);
  }

  return (
    <div className="add-user">
      <form onSubmit={handleAddUser}>
        <label>
          Username:
          <input value={username} onChange={onChange} />
          {isSuccess && <SuccessMessage message={"User added successfully."} />}
          {isError && <ErrorMessage message={error.message} />}
          <ButtonWithSpinner
            isLoading={false}
            type="submit"
            disabled={isLoading}
          >
            Create Channel
          </ButtonWithSpinner>
        </label>
      </form>
    </div>
  );
}
