import { client } from "../utils/apiClient";

export async function login({
  username,
  firstname,
  lastname,
}: {
  username: string;
  firstname: string;
  lastname: string;
}) {
  return client("/users/login", null, {
    method: "POST",
    data: {
      username,
      name: `${firstname} ${lastname}`,
    },
  });
}
