import { useEffect } from "react";

function usePolling(cb: () => void, deps: any[]) {
  useEffect(() => {
    let timerId: number;

    (async function pollMessages() {
      cb();
      timerId = window.setTimeout(pollMessages, 5000);
    })();

    return () => {
      clearTimeout(timerId);
    };
  }, []);
}
