import { ListItem } from "./ListItem";
import { useCollapse } from "../../hooks/useCollapse";
import { UserType } from "../main/types";

type Props = {
  title: string;
  users: UserType[];
  selectedUserId: string;
  onUserChange: any;
};

export function List({ title, users, selectedUserId, onUserChange }: Props) {
  const { open, toggle } = useCollapse(false);

  return (
    <dl className="user-list">
      <dt onClick={() => toggle()}>
        <div className={"arrow" + (open ? " arrow--down" : "")}></div>
        {title}
      </dt>
      {open &&
        users.map((user) => (
          <ListItem
            key={user.id}
            user={user}
            selectedUserId={selectedUserId}
            onUserChange={onUserChange}
          />
        ))}
    </dl>
  );
}
