import { useState } from "react";
import { ChatFeed } from "./ChatFeed";
import userLogo from "../../user.png";
import { MESSAGE_TYPE } from "../../constants/index";
import { getMessages } from "../../helpers/index";
import { MessageType } from "../../types";
import { useToggle } from "../../hooks/useToggle";
import { Modal } from "../Modal";
import { useEffect } from "react";

type Props = {
  chatType: MESSAGE_TYPE;
  id: string;
};

export function Chat({ chatType, id }: Props) {
  const { open, toggle } = useToggle(false);
  const [messages, setMessages] = useState<MessageType[]>([]);

  useEffect(() => {
    // fetching messages from backend
    // signature needs to be changed
    // polling will be done here
    setMessages(getMessages(chatType, id));
  }, [chatType, id]);

  return (
    <div className="chat">
      <header className="chat__header">
        <img className="chat__icon" src={userLogo} />
        <p>{"Rishi Goel"}</p>
        {chatType === MESSAGE_TYPE.CHANNEL &&
          (<span className="chat__members" onClick={toggle}>
            <img className="chat-members__icon" src={userLogo} />
            <p className="chat__members-count">{8}</p>
          </span>)(open ? <Modal>{1}</Modal> : null)}
      </header>
      <ChatFeed messages={messages} />
      <footer className="chat__footer">
        <form>
          <input className="chat__message-input" placeholder="Message"></input>
          <button type="submit" className="chat__send-btn">
            Send
          </button>
        </form>
      </footer>
    </div>
  );
}
