import userIcon from "./user.png";
import { MessageType } from "./fixture";

function Message({ message }: { message: MessageType }) {
  return (
    <div className="message">
      <img src={userIcon} className="message__user-img" />
      <div className="message__body">
        <header className="message__header">
          <h3 className="message__username">{message.from}</h3>
          <p className="message__time">{message.createdAt}</p>
        </header>
        <p className="message__text">{message.text}</p>
      </div>
    </div>
  );
}

export function ChatFeed({ messages }: { messages: MessageType[] }) {
  return (
    <div className="chat__feed">
      {messages.map((message) => {
        return <Message key={message.id} message={message} />;
      })}
    </div>
  );
}
