import { UserType } from "../main/types";
import userIcon from "../../user.png";

type Props = {
  item: UserType;
  selectedUserId: string;
  onItemChange: any;
};

export function ListItem({ item, selectedUserId, onUserChange }: Props) {
  return (
    <dd
      className={
        "list-item" + (item.id === selectedId ? " list-item--selected" : "")
      }
      onClick={() => onItemChange(item.id)}
    >
      <img src={itemIcon} className="list-item__img" />
      <p className="list-item__name">{item.fullname}</p>
      {item.unreadCount > 0 && (
        <div className="list-item__unread">{item.unreadCount}</div>
      )}
    </dd>
  );
}
