import { useEffect, useState } from "react";
import { useToggle } from "../../hooks/useToggle";
import { Modal } from "../Modal";
import { getMembers } from "../../helpers/index";
import { KnownUserType } from "../../types";
import {} from "../collapsibleList/ListItem";
import userLogo from "../../user.png";

type Props = {
  channelId: string;
};

export function ChannelMembers({ channelId }: Props) {
  const { open, toggle } = useToggle(false);
  const [members, setMembers] = useState<KnownUserType[]>([]);

  useEffect(() => {
    setMembers(getMembers(channelId));
  }, []);

  return (
    <>
      <span className="chat__members" onClick={toggle}>
        <img className="chat-members__icon" src={userLogo} />
        <p className="chat__members-count">{8}</p>
      </span>
      {open && (
        <Modal>
          <div className="modal-container">
            <div className="chat__members-modal">
              <header className="chat__members-modal-hedear">
                <button onClick={toggle}>X</button>
              </header>
              <ListItem
                selected={channel.id === "2"}
                onClick={handleChannelChange}
              >
                <ListItemIcon src={channelIcon} />
                <ListItemText text={channel.name} />
                {channel.unreadCount > 0 && (
                  <ListItemBadge text={channel.unreadCount.toString()} />
                )}
              </ListItem>
            </div>
          </div>
        </Modal>
      )}
    </>
  );
}
