import { useCollapse } from "../../hooks/useCollapse";

export function ListItemBadge({ text }: { text: string }) {
  return <span className={"list-item__badge"}>{text}</span>;
}

export function ListItemText({ text }: { text: string }) {
  return <p className="list-item__text">{text}</p>;
}

export function ListItemIcon({ src }: { src: string }) {
  return <img className="list-item__icon" src={src} />;
}

type ListItemProps = {
  selected: boolean;
  onClick?: React.MouseEventHandler<HTMLElement>;
  children: React.ReactNode;
};

export function ListItem({ selected, onClick, children }: ListItemProps) {
  return (
    <dd
      className={"list-item" + (selected ? " list-item--selected" : "")}
      onClick={onClick}
    >
      {children}
    </dd>
  );
}

type ListProps = {
  title: string;
  onChange: any;
  children: React.ReactNode;
};

export function CollapsibleList({ title, onChange, children }: ListProps) {
  const { open, toggle } = useCollapse(true);

  return (
    <dl className="list">
      <dt className="list__title" onClick={() => toggle()}>
        <div className={"arrow" + (open ? " arrow--down" : "")}></div>
        {title}
      </dt>
      {open && children}
    </dl>
  );
}
