import { ChatFeed } from "./ChatFeed";
import userLogo from "../../user.png";
import { UserType } from "../../types";

type Props = {
  user: UserType;
};

export function Chat({ user }: Props) {
  function handleMessage() {}

  return (
    <div className="chat">
      <header className="chat__header">
        <img className="chat__icon" src={userLogo} />
        <p>{user.name}</p>
      </header>
      <ChatFeed messages={user.messages} />
      <footer className="chat__footer">
        <form onSubmit={handleMessage}>
          <input className="chat__message-input" placeholder="Message"></input>
          <button type="submit" className="chat__send-btn">
            Send
          </button>
        </form>
      </footer>
    </div>
  );
}
