import { ListItem } from "./ListItem";
import { useCollapse } from "../../hooks/useCollapse";
import { UserType } from "../main/types";

type Props = {
  title: string;
  items: UserType[];
  selectedId: string;
  onChange: any;
};

export function List({ title, items, selectedId, onChange }: Props) {
  const { open, toggle } = useCollapse(true);

  return (
    <dl className="list">
      <dt className="list__title" onClick={() => toggle()}>
        <div className={"arrow" + (open ? " arrow--down" : "")}></div>
        {title}
      </dt>
      {open &&
        items.map((item) => (
          <ListItem
            key={item.id}
            item={item}
            selectedId={selectedId}
            onChange={onChange}
          />
        ))}
    </dl>
  );
}
