import {
  ListItem,
  ListItemIcon,
  ListItemText,
  ListItemBadge,
} from "./ListItem";
import { CollapsibleList } from "./CollapsibleList";
import { UserType, ChannelType } from "../main/types";
import userIcon from "../../user.png";
import channelIcon from "../../hashtag.png";

type Props = {
  users: UserType[];
  channels: ChannelType[];
  selectedUserId: string;
  onUserChange: any;
  onChannelChange: any;
};

export function Sidebar({
  users,
  channels,
  selectedUserId,
  onUserChange,
  onChannelChange,
}: Props) {
  return (
    <div className="sidebar">
      <header className="sidebar__header"></header>
      <CollapsibleList title="Channels" onChange={onChannelChange}>
        {channels.map((channel) => (
          <ListItem selected={channel.id === selectedUserId}>
            <ListItemIcon src={channelIcon} />
            <ListItemText text={channel.name} />
            {channel.unreadCount > 0 && (
              <ListItemBadge text={channel.unreadCount.toString()} />
            )}
          </ListItem>
        ))}
      </CollapsibleList>
      <CollapsibleList title="Direct messages" onChange={onUserChange}>
        {users.map((user) => (
          <ListItem selected={user.id === selectedUserId}>
            <ListItemIcon src={userIcon} />
            <ListItemText text={user.name} />
            {user.unreadCount > 0 && (
              <ListItemBadge text={user.unreadCount.toString()} />
            )}
          </ListItem>
        ))}
      </CollapsibleList>
    </div>
  );
}
