import {
  ListItem,
  ListItemIcon,
  ListItemText,
  ListItemBadge,
  CollapsibleList,
} from "../collapsibleList/index";
import { UserType, ChannelType } from "../main/types";
import userIcon from "../../user.png";
import channelIcon from "../../hashtag.png";

type Props = {
  users: UserType[];
  channels: ChannelType[];
  selectedUserId: string;
  onUserChange: any;
  onChannelChange: any;
};

export function Sidebar({
  users,
  channels,
  selectedUserId,
  onUserChange,
  onChannelChange,
}: Props) {
  function handleChannelChange(e: React.MouseEventHandler<HTMLElement>) {
    e.stopPropagation();
    onChannelChange();
  }

  function handleUserChange(e: React.MouseEventHandler<HTMLElement>) {
    e.stopPropagation();
    onUserChange();
  }

  return (
    <div className="sidebar">
      <header className="sidebar__header"></header>
      <CollapsibleList title="Channels">
        {channels.map((channel) => (
          <ListItem
            selected={channel.id === selectedUserId}
            onClick={handleChannelChange}
          >
            <ListItemIcon src={channelIcon} />
            <ListItemText text={channel.name} />
            {channel.unreadCount > 0 && (
              <ListItemBadge text={channel.unreadCount.toString()} />
            )}
          </ListItem>
        ))}
      </CollapsibleList>
      <CollapsibleList title="Direct messages">
        {users.map((user) => (
          <ListItem
            selected={user.id === selectedUserId}
            onClick={handleUserChange}
          >
            <ListItemIcon src={userIcon} />
            <ListItemText text={user.name} />
            {user.unreadCount > 0 && (
              <ListItemBadge text={user.unreadCount.toString()} />
            )}
          </ListItem>
        ))}
      </CollapsibleList>
    </div>
  );
}
