import { useCollapse } from "../../hooks/useCollapse";
import { UserType } from "../main/types";

export function Badge({ text }: { text: string }) {
  return <span className={"badge"}>{text}</span>;
}

export function ListItemIcon({ src }: { src: string }) {
  return <img className="list-item__icon" src={src} />;
}

type ListItemProps = {
  item: UserType;
  selected: boolean;
  onChange: React.MouseEventHandler<HTMLElement>;
};

export function ListItem({ item, selected, onChange }: ListItemProps) {
  return (
    <dd
      className={"list-item" + (selected ? " list-item--selected" : "")}
      onClick={onChange}
    >
      <p className="list-item__text">{item.name}</p>
    </dd>
  );
}

type ListProps = {
  title: string;
  onChange: any;
};

export function CollapsibleList({ title, onChange }: ListProps) {
  const { open, toggle } = useCollapse(true);

  return (
    <dl className="list">
      <dt className="list__title" onClick={() => toggle()}>
        <div className={"arrow" + (open ? " arrow--down" : "")}></div>
        {title}
      </dt>
    </dl>
  );
}
