import { UserList } from "../sidebar/UserList";
import { UserType } from "../main/types";

type Props = {
  users: UserType[];
  selectedUserId: string;
  onUserChange: any;
};

export function Sidebar({ users, selectedUserId, onUserChange }: Props) {
  return (
    <div className="sidebar">
      <header className="sidebar__header"></header>
      <UserList
        users={users}
        selectedUserId={selectedUserId}
        onUserChange={onUserChange}
      />
    </div>
  );
}
