import { useCollapse } from "../../hooks/useCollapse";
import "./styles.css";

type Props = {
  title: string;
  onChange: any;
  children: React.ReactNode;
};

export function CollapsibleList({ title, onChange, addItem, children }: Props) {
  const { open, toggle } = useCollapse(true);

  return (
    <dl className="list">
      <dt className="list__title" onClick={() => toggle()}>
        <div className={"arrow" + (open ? " arrow--down" : "")}></div>
        {title}
        <div className="list__add-item" onClick={addItem}>
          +
        </div>
      </dt>
      {open && children}
    </dl>
  );
}
