import { UserType } from "../main/types";
import userIcon from "../../user.png";

type Props = {
  user: UserType;
  selectedUserId: string;
  onUserChange: any;
};

export function UserListItem({ user, selectedUserId, onUserChange }: Props) {
  return (
    <div
      className={
        "user-list-item" +
        (user.id === selectedUserId ? " user-list-item--selected" : "")
      }
      onClick={() => onUserChange(user.id)}
    >
      <img src={userIcon} className="user-list-item__img" />
      <p className="user-list-item__username">{user.fullname}</p>
      {user.unreadCount && (
        <div className="user-list-item__unread">{user.unreadCount}</div>
      )}
    </div>
  );
}
