import axios from "axios";
import {
  knownUsers,
  users,
  knownChannels,
  channels,
  messages,
} from "./fixture";
import { MESSAGE_TYPE } from "../constants/index";

async function delay(ms: number) {
  await new Promise((resolve, reject) => {
    setTimeout(() => resolve(""), ms);
  });
}

export async function getUsers() {
  await delay(1000);
  return users;
}

export async function getKnownUsers() {
  await delay(1000);
  return knownUsers;
}

export async function getChannels() {
  await delay(1000);
  return channels;
}

export async function getKnownChannels() {
  await delay(1000);
  return knownChannels;
}

export async function getMessages(type: MESSAGE_TYPE, id: string) {
  await delay(1000);
  return messages;
}

export async function getMembers(channelId: string) {
  await delay(1000);
  return knownUsers;
}

export async function sendMessageAPI(date: {
  from: string;
  fromId: string;
  to: string;
  toId: string;
  text: string;
}) {
  await delay(1000);
  return;
}

export async function loginAPI({
  username,
  firstname,
  lastname,
}: {
  username: string;
  firstname: string;
  lastname: string;
