import userIcon from "./user.png";
import { UserType, users } from "./fixture";

function UserListItem({ user }: { user: UserType }) {
  return (
    <div className="user-list-item">
      <img src={userIcon} className="user-list-item__img" />
      <h3>{user.name}</h3>
      <div className="user-list-item__unread">{user.unReadCount}</div>
    </div>
  );
}

export function UserList() {
  return (
    <ul className="list">
      {users.map((user) => (
        <UserListItem user={user} />
      ))}
    </ul>
  );
}
