import { useCollapse } from "../../hooks/useCollapse";
import { UserType } from "../main/types";

type ListItemProps = {
  item: UserType;
  selected: boolean;
  onChange: React.MouseEventHandler<HTMLElement>;
};

export function ListItemIcon({ src }) {
  return <img className="list-item__icon" src />;
}

export function ListItem({ item, selected, onChange }: ListItemProps) {
  return (
    <dd
      className={"list-item" + (selected ? " list-item--selected" : "")}
      onClick={onChange}
    >
      <img src={userIcon} className="" />
      <p className="list-item__name">{item.name}</p>
    </dd>
  );
}

type ListProps = {
  title: string;
  items: UserType[];
  selectedId: string;
  onChange: any;
};

export function List({ title, items, selectedId, onChange }: ListProps) {
  const { open, toggle } = useCollapse(true);

  return (
    <dl className="list">
      <dt className="list__title" onClick={() => toggle()}>
        <div className={"arrow" + (open ? " arrow--down" : "")}></div>
        {title}
      </dt>
      {open &&
        items.map((item) => (
          <ListItem
            key={item.id}
            item={item}
            selectedId={selectedId}
            onChange={onChange}
          />
        ))}
    </dl>
  );
}
